trigger_prototype.expression:
{Template Server HP iLO SNMPv2:sensor.temp.threshold[cpqHeTemperatureThreshold.{#SNMPINDEX}].last()}<>0 
and
{Template Server HP iLO SNMPv2:sensor.temp.value[cpqHeTemperatureCelsius.{#SNMPINDEX}].last(5m)}>={Template Server HP iLO SNMPv2:sensor.temp.threshold[cpqHeTemperatureThreshold.{#SNMPINDEX}].last(5m)}-{$TEMP_WARN}

trigger_prototype.recovery_expression:
{Template Server HP iLO SNMPv2:sensor.temp.threshold[cpqHeTemperatureThreshold.{#SNMPINDEX}].last()}<>0 
and
{Template Server HP iLO SNMPv2:sensor.temp.value[cpqHeTemperatureCelsius.{#SNMPINDEX}].last(5m)}+{$TEMP_WARN} <{Template Server HP iLO SNMPv2:sensor.temp.threshold[cpqHeTemperatureThreshold.{#SNMPINDEX}].last(5m)}
